(window.webpackJsonp=window.webpackJsonp||[]).push([[22],{512:function(v,_,r){"use strict";r.r(_);var n=r(4),p=Object(n.a)({},(function(){var v=this,_=v.$createElement,r=v._self._c||_;return r("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[r("h1",{attrs:{id:"死亡"}},[r("a",{staticClass:"header-anchor",attrs:{href:"#死亡"}},[v._v("#")]),v._v(" 死亡")]),v._v(" "),r("p",[v._v("我对死亡似乎没有什么实感"),r("br"),v._v("\n身边亲近的人都在"),r("br"),v._v("\n除了我的爷爷"),r("br"),v._v("\n但那也是我小学时候的事了"),r("br")]),v._v(" "),r("p",[v._v("我爷爷当时是已经病了好久了"),r("br"),v._v("\n我觉得他会好起来的"),r("br"),v._v("\n有时候也会呆在他床边陪着他"),r("br"),v._v("\n叫爷爷 他也会睁开眼睛来看我"),r("br"),v._v("\n得知他去世的时候有一种强烈的不真实感"),r("br"),v._v("\n这也是我第一次经历亲人的死亡")]),v._v(" "),r("p",[v._v("但我当时好像并不难过"),r("br"),v._v("\n我觉得我应该是要难过的"),r("br"),v._v("\n但是我哭不出来")]),v._v(" "),r("p",[v._v("我小时候爷爷很疼我，爸爸妈妈上班去了，他怕我无聊，蹬着三轮车带我出去玩。去了哪只有一些模糊的印象。好像是往家东边的方向，那边是一大片田，田的中间有一颗大树，树下有一座关公庙。之后好像还去过榨油厂，那里面很热，我至今都还记得那吹出热风的电风扇。那时的我一般只在家附近活动，这些地方对我来说已经很新鲜了。")]),v._v(" "),r("p",[v._v("记得爷爷奶奶之前是在家里做拖把，赚点小钱。而我小时候没事干，就喜欢去帮忙。奶奶负责把铁丝掰弯，爷爷负责把铁丝穿过两个零件，将他们组装起来。而我则是拿着一个小榔头，把连接处敲紧。有一次我逞能，去抢奶奶的活干，去掰弯铁丝。铁丝的截断处是很锋利的，一不小心我的手心就被划去了，我嚎啕大哭。弄得爷爷奶奶很心疼，好长时间都不让我再去帮忙。")]),v._v(" "),r("p",[v._v("刚开始写这篇文章的时候，我奶奶还在"),r("br"),v._v("\n我在这记录关于我爷爷的回忆"),r("br"),v._v("\n可是开始动笔后没多久，我奶奶也去世了"),r("br"),v._v("\n奶奶的葬礼办得很隆重，我依然没有哭"),r("br"),v._v("\n火化的那天，奶奶的旧衣物和家具也一并烧掉了"),r("br"),v._v("\n世上能证明其存在的，只剩下了那块乌黑的石头，以及我们对她的回忆"),r("br"),v._v("\n那么让我在这再添上点关于她老人家的回忆吧")]),v._v(" "),r("p",[v._v("奶奶在爷爷走后不久就患了阿尔兹海默症，一直需要有人贴身照顾。由她的几个孩子，也就是我的大伯、姑姑以及我的爸爸轮流照顾。几年里我看着她越来越糊涂，越来越不像是我熟悉的奶奶。她开始讲不清话，开始乱走，开始找不到回家的路。有一次，大门忘记关，奶奶走了出去。我们发动全家出去找，最后在铁路边的田埂上找到了她。我家的狗子就坐在她身边，大概也是不放心她一个人走出来。")]),v._v(" "),r("p",[v._v("还有一段由我妈讲述的奶奶的童年，是奶奶还清醒的时候讲给她听的。")]),v._v(" "),r("p",[v._v("我爷爷死后，奶奶兄弟几个轮流照顾，这家住几个月，那家住几个月。她说这样的生活让她想起了小时候，日本鬼子打来了，他们这些小孩就是在这家躲几天，那家躲几天。最后，村里的大人把小孩都藏到了山上。小时候不懂事嘛，不知道日本鬼子来了是什么意思，反而觉得这样的日子很好玩。")]),v._v(" "),r("p",[v._v("奶奶的葬礼上，有一些亲戚家的孩子。年纪尚小，还不知道的死亡的意义。在那看着我，傻傻的笑着。老一辈的死去，又有新一辈的诞生，这便是名为生命的轮回。")]),v._v(" "),r("p",[r("em",[v._v("死亡，与新生")])]),v._v(" "),r("p",[v._v("【人】或许会死亡，但【人类】不会消失")]),v._v(" "),r("p",[v._v("害怕死亡是正常的，大家都害怕死亡，习惯性对死字避而不谈。"),r("br"),v._v("\n俄国哲学家别尔嘉耶夫说过：人生在世之所以会有意义，就是因为有死亡这件事。假如人间没有死，人生的意义就消失了。"),r("br"),v._v("\n因为有死亡，人们才会想起在这个有限且短暂的生命过程中创造些什么，这样的想法也推动着人们不断前进。"),r("br"),v._v("\n倘若没有死亡，便意味着有无限的时光可以浪费。当“时间”不再是稀有资源，失去有限的生命的鞭策，拖延的心理会不断蔓延。渐渐的，丧失动力、丧失欲望、丧失一切。"),r("br"),v._v("\n倘若没有死亡，也就不再需要繁衍，基因的延续这一最原始的动力也消失了。")]),v._v(" "),r("h2",{attrs:{id:"大伯"}},[r("a",{staticClass:"header-anchor",attrs:{href:"#大伯"}},[v._v("#")]),v._v(" 大伯")]),v._v(" "),r("p",[r("em",[v._v("2023年2月3日添")])]),v._v(" "),r("p",[v._v("奶奶走后半年，轮到大伯了")]),v._v(" "),r("p",[v._v("大伯，论辈分应该是叫四伯的，奶奶生了五个儿子，但我觉得只有这个是亲的大伯，其他几个都不常联系。阿姨是大伯的妻子，应该是要叫她大妈，但小时候叫习惯了，她说不用改口，这样挺好的。我对阿姨的过往没什么了解，只有些大人们聊天时听来的只言片语。她和大伯是再婚，她小时候是在孤儿院长大的。大伯和阿姨都是盲人，不过大伯是半盲，还算是能看见东西的。")]),v._v(" "),r("p",[v._v("他们一起开了家盲人推拿店，起初是开在家这边，后来搬到了东关。在我幼儿园的时候，妈妈带着我，在大伯的推拿店帮忙，平时也都是住在这里，只有过年的时候回去。还在上初中的姐姐则是在家跟着爸爸。但正因如此，我的童年是缺少父爱的，可能这也是导致了我现在依然和爸爸有些疏离感的原因。大伯则会带着我买东西、吹气球、折纸飞机、逛公园，有时候放学都是他来接我。有时候，还会偷偷给我塞点零花钱。或许小时候的我认为，他更像是我的“爸爸”吧。")]),v._v(" "),r("p",[v._v("大伯年轻的时候，算是放荡不羁爱自由。他学过算命，学过乐器，还会唱歌，买了二胡、笛子、萧、葫芦丝、唢呐还有音响和麦克风。这些东西都是偷偷藏私房钱瞒着阿姨买的。有时候，还会叫上好朋友到家里来上一场音乐会。每每这种时候，阿姨都要嫌他太吵闹，他的萧和笛子，阿姨都给他扔掉过几次。")]),v._v(" "),r("p",[v._v("我的高中就在大伯的店附近，还想着以后周末去玩很方便。结果意外就发生在我高一那年，刚结束英语等级考试的我接到消息——大伯中风了。这对一家人来说无疑是晴天霹雳，赶到医院的时候，我只看到大伯躺在病床上。手术风险很高，阿姨选择了保守治疗。在医院住院了大半年后，阿姨把推拿店转让给了朋友，和大伯搬回了家里这边住。大伯和我爸爸兄弟两个关系非常好，我们两家连房子都是连在一起的，能直接走过去。")]),v._v(" "),r("p",[v._v("这个时候，大伯还可以自己拄着拐杖，慢慢的走动，还能自己吃饭。大伯有糖尿病，每次饭都吃得不多，盛的多了，他还要不高兴。而我能做的，也只有在家的时候每天晚上去给他打胰岛素。像上厕所、洗澡和换衣服之类的，就需要阿姨伺候，而阿姨也无怨无悔的照顾着。阿姨是全盲，却学会了洗衣服做饭。只需要我帮她在电饭锅、微波炉这些电器的按钮上做上记号，她就能自己使用了。")]),v._v(" "),r("p",[r("em",[v._v("“你照顾我半辈子了，这后半辈子该轮到我照顾你了。”")]),r("br"),v._v(" "),r("em",[v._v("“明明是该我来照顾你才对的，唉。”")])]),v._v(" "),r("p",[v._v("这样的生活持续了一年左右，我以为也会继续这样下去。")]),v._v(" "),r("p",[v._v("其实在住院期间以及往后的日子里，大伯不止一次的表示，不想这样活着，想要死了算了。要求我们去医院给他申请安乐死的针。这种卧病在床，处处要让人照顾的日子，他实在是受不了，他也不想让亲人这么受累。")]),v._v(" "),r("p",[v._v("年关将至，大伯在这个时候摔了一跤，去医院看了并没有什么大碍。但是大伯此后便卧床不起，一蹶不振。喂他东西也不肯吃，每天只喝一点点牛奶。眼睛一眨一眨，和他讲话也没有回应。这副样子，让我想起了弥留之际的爷爷。")]),v._v(" "),r("p",[v._v("大伯去世的三天前，脚上起了个大水泡，是阿姨早上帮大伯脱袜子，摸去湿漉漉的才知道。可是这正月里上哪去找医生呢？我认为他是被热水袋烫伤的，因为阿姨怕他冷，在他的脚后放了一个热水袋。皮肤长时间接触高于体表的温度引起的低温烫伤。我和堂姐就去药店买了烫伤药膏。而后来赶到的堂哥却认为这是长期卧床导致的褥疮，用了治褥疮的药辅以龙血竭胶囊药粉，然后包扎起来。不过这些药的效果应该都是杀菌止血、消肿止痛之类的，应该也差不多。后面的两天晚上，我们都给大伯换了药。第四天的时候，我早上起床，看到楼下聚了很多亲戚，堂姐的眼睛红红的，让我去吃早饭。虽然谁都没说，但是我已经知道发生什么事了，大伯究竟是烫伤还是褥疮也已经不重要了。")]),v._v(" "),r("p",[v._v("凌晨的时候，我捧着相片，跟在堂姐的身后，走在送行队伍的前面。明明前几天晚上都还是繁星点点，月光皎洁。而今天却是阴云密布，寒风凛冽。")]),v._v(" "),r("p",[v._v("大伯的遗物里有好多根笛子和萧，还有几把二胡以及其他的一些乐器。阿姨挑了几样比较新的，送给了我和爸爸，也算是做个纪念。剩下的都烧给那个贪玩的大伯了。")]),v._v(" "),r("p",[v._v("爸爸留了两根萧，而我留了一把二胡和葫芦丝。")]),v._v(" "),r("p",[v._v("葬礼上没哭，这回忆起来，眼泪却止不住的往下掉了。")])])}),[],!1,null,null,null);_.default=p.exports}}]);